import React, { Component } from 'react'
import axios from 'axios'

 class DeleteTodo extends Component {
     state = {
         discription: '',
         responsible: '',
         priority: '',
         isCompleted: false
     }

     onChangeTodoHandler = (e) => {
         this.setState({
             [e.target.name]: e.target.value
         })

     }

     componentDidMount() {
         axios.get("http://localhost:5000/" + this.props.match.params.id)
             .then(res => {
                 console.log(res)
                 this.setState({
                     discription: res.data.discription,
                     responsible: res.data.responsible,
                     priority: res.data.priority,
                     isCompleted: res.data.isCompleted
                 })
             })
             .catch(error => console.log(`Error fetch API ${error}`))
     }


     deleteHandler = () => {
         e.preventDefault();
         const updatedTodo = {
             discription: this.state.discription,
             responsible: this.state.responsible,
             priority: this.state.priority,
             isCompleted: false
         }

         
         axios.delete('http://localhost:5000/' + this.props.match.params.id)
             .then(console.log('Deleted'))
             .catch(err => console.log(err))
     }

    render() {
        return (
            <div>
                Delete page
            </div>
        )
    }
}
export default DeleteTodo
