class CreateTodo extends Component {
    state = {
        todo_discription: '',
        todo_responsible: '',
        todo_priority: '',
        todo_completed: false
    }

    onChangeTodoHandler = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    onSubmit(e) {
        e.preventDefaulth();
        console.log(this.state)
    }
    render() {
        return (
            <div className="container">
                <div className="jumbotron">
                    <h3>Create New Todo</h3>
