import React, { Component } from 'react'

class CreateTodo extends Component {
    state = {
        todo_discription: '',
        todo_responsible: '',
        todo_priority: '',
        todo_completed: false
    }

    onChangeTodoHandler = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
        console.log(this.state)
    }

    onSubmit(e) {
        e.preventDefault();
        console.log(this.state)
    }
    render() {
        return (
            <div className="container">
                <div className="jumbotron w">
                    <h3>Create New Todo</h3>
                    <form onSubmit={this.onSubmit}>
                        <div className="form-group">
                            <label htmlFor="discription">Discription</label>
                            <input type="text" name="discription" onChange={this.onChangeTodoHandler} className="form-control" id="exampleFormControlInput1" placeholder="type in something todo" />
                       </div>
                        <div className="form-group">
                            <label htmlFor="responsible">Responsible</label>
                            <input type="text" name="responsible" onChange={this.onChangeTodoHandler} className="form-control" id="exampleFormControlInput1" placeholder="type in something todo" />
                        </div>
                        <div className="form-group">
                            <div className="form-check form-check-inline">
                                <input className="form-check-input" type='radio' name="priorityOptions" id="priorityLow"
                                    value="Low" checked={this.state.todo_priority === 'Low'} onChange={this.onChangeTodoHandler} />
                                <label className="form-check-label">Medium</label>
                            </div>
                            <div className="form-check form-check-inline">
                                <input className="form-check-input" type="radio" name="priorityOptions" id="priorityHigh"
                                    value="High" checked={this.state.todo_priority === 'High'} onChange={this.onChangeTodoPriority}/>
                                <label className="form-check-label">High</label>
                            </div>
                            </div>
                        {/* </div> */}
                        <button type="submit" className="btn btn-primary ">Add</button>

                   </form>
                </div>
                </div>
                )
            }
        }
        
export default CreateTodo
